import React, { useEffect, useState } from 'react';
import { Upload, Button, message, Image, Space } from 'antd';
import { LoadingOutlined, PlusOutlined, UploadOutlined } from '@ant-design/icons';
import './index.css';
import styles from './style.less';

function beforeUpload(file) {
  const isJpgOrPng = file.type === 'image/jpeg' || file.type === 'image/png';
  if (!isJpgOrPng) {
    message.error('You can only upload JPG/PNG file!');
  }
  const checkImgSize = file.size / 1024 / 1024 < 5;
  if (!checkImgSize) {
    message.error('Image must smaller than 5MB!');
  }
  return isJpgOrPng && checkImgSize;
}

const failureImg =
  'data:image/png;base64,';

const ImageUploader = ({ multiple = false, onChange: onChangeForm, ...props }) => {
  const [fileList, setFileList] = useState(props.fileList != null ? props.fileList : null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setFileList(props.fileList != null ? props.fileList : null);
    fetch('https://localhost:44337/api/v1/brands')
    .then(response => response.json())
    
    
  }, [props.fileList]);
  
  const defaultProps = {
    beforeUpload,
    onChange: (info) => {
      if (info.file.status === 'uploading') {
        setLoading(true);
        return;
      }
      if (info.file.status === 'done') {
        const fileData = multiple ? info.fileList : info.file;
        onChangeForm({ fileList: multiple ? fileData : [fileData] });
        setFileList(multiple ? fileData : [fileData]);
        setLoading(false);
        return;
      }
      if (info.file.status === 'error') {
        message.error('Lá»—i khi upload file');
        setLoading(false);
      }
    },
  };

  const fileResList = () => {
    if (fileList == null) {
      return null;
    }
    if (fileList && !multiple) {
      const { name, response: imgUrl } = fileList[0];
      const src = imgUrl !== undefined ? imgUrl : fileList;
      return (
        <Image
          className={styles.imageContainer}
          style={{ width: '102px', height: '102px' }}
          width={102}
          height={102}
          alt={name}
          src={src}
          fallback={failureImg}
        />
      );
    }
  };

  const uploadButton = (
    <div>
      {loading ? <LoadingOutlined /> : <PlusOutlined />}
      <div style={{ marginTop: 8 }}>Upload</div>
    </div>
  );

  return (
    <Space direction="horizontal" style={{ display: 'flex' }}>
      {fileResList()}
      <Upload
        // name="avatar"
        className="avatar-uploader"
        listType="picture-card"
        action={`${API_URL}/brands`}
        // action="https://192.168.1.7:45455/api/v1/brands"
        showUploadList={false}
        style={{ width: 250 }}
        multiple={multiple}
        // {...props}
        {...defaultProps}
      >
        {uploadButton}
      </Upload>
    </Space>
  );
};

export default ImageUploader;
